(3, 'newsletter', '{error}\r\n<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\r\n<fieldset class=\"formContainer uk-form-horizontal\" id=\"rsform_{global:formid}_page_0\">\r\n	<div class=\"uk-position-relative\">\r\n		<div class=\"rsform-block-email\">\r\n			<div class=\"uk-form-controls formControls uk-width-1-1 uk-margin-remove\">\r\n				{email:body}\r\n				<span class=\"formValidation\">{email:validation}</span>\r\n			</div>\r\n		</div>\r\n		<div class=\"uk-position-absolute uk-position-center-right\">\r\n			<div class=\"uk-form-controls formControls uk-margin-remove\">{submit:body}</div>\r\n		</div>\r\n	</div>\r\n	{if {email:description}}{email:description}{/if}\r\n</fieldset>', '[[{\"columns\":[[\"23\",\"24\"]],\"sizes\":[\"12\"]}],[]]', 'uikit3', 0, 0, 0, 0, 0, '', '<script>\r\njQuery(document).ready(function(){\r\njQuery(\'form.newsletter button#submit\').html(\'<i class=\"fas fa-envelope\"></i>\');\r\n})\r\n</script>', 'Newsletter', 1, 1, 'en-GB', '', 1, 1, 0, 0, '', 1, '', '', '', '', '{global:mailfrom}', '', '{global:fromname}', '', 1, 0, '', '', '', '', '', '', '', '', '', 1, '', '', '', '', '{global:mailfrom}', '', '{global:fromname}', '', 1, '', '', '', '', '', '', '', '', 0, '', '', '(*)', '<p class=\"formRed\">Please complete all required fields!</p>', '\\n', 1, 'newsletter uk-margin-medium-bottom', 'userForm', '', '', '', 0, 0, 1, 1, 0, 0, 0, '', ''),
